import Header from './Header';
import { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';

function Book() {
    const username = JSON.parse(window.localStorage.getItem('user-info'));
    const [isLoading, setIsLoading] = useState(false);
    useEffect(() => {
        let data = { 'email': document.getElementById("emailLogin").value, 'password': document.getElementById("inputPassword").value }
        const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' },
        body: JSON.stringify(data)
    };
        fetch("http://127.0.0.1:8000/api/login", requestOptions)
            .then((respose) => respose.json())
            .then((respose) => {
                //    setUsers(respose.data)
                if (respose.token) {
                    localStorage.setItem('user-info', JSON.stringify(respose));
                    toast("Logged in successfully", {
                        position: toast.POSITION.TOP_CENTER,
                        className: 'toast-success',
                        progressClassName: 'success-progress-bar',
                        autoClose: 2000,
                        toastId: 1
                    });
                } else {
                    setIsLoading(false);
                    toast("Logged in Failed", {
                        position: toast.POSITION.TOP_CENTER,
                        className: 'toast-error',
                        progressClassName: 'error-progress-bar',
                        autoClose: 2000,
                        toastId: 2
                    });
                }

            })
            .catch(() => {
                setIsLoading(false);
                toast("Unable to send request", {
                    position: toast.POSITION.TOP_CENTER,
                    className: 'toast-error',
                    progressClassName: 'error-progress-bar',
                    autoClose: 2000,
                    toastId: 1
                });
            });
    });
  return (
    <>
    
    <Header />
    <h2>{localStorage.getItem('user-info') ? username.name : ""} you bookings.</h2>
    <form>
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="emaillogin">Email</Form.Label>
                                <Form.Control size="lg" value={message} id="emailLogin" type="email" name="emaillogin" placeholder="User Email" onChange={(e) => setMessage(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Please enter email.</Form.Control.Feedback>
                            </Form.Group>
    </form>
        <div id="list">

        </div>
    </>
  );
}

export default Book;
