import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState, useEffect } from 'react';
import {useNavigate} from 'react-router-dom';
import LoadingSpinner from "./LoadingSpinner";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Header from './Header';

function Register() {
    const history = useNavigate();
    useEffect(() => {
        if (window.localStorage.getItem('user-info') !== null) {
            history('/');
        }
    });
  return (
    <>
    
    <Header />
    <div className='col-xs-6 col-sm-6 col-md-6 offset-xs-3 offset-sm-3 offset-md-3 register-form'>
    <h2>Register</h2>
        <Card style={{ width: '100%' }}>
      <Card.Body>
        <Card.Text>
        <Form.Label htmlFor="namereg">Name</Form.Label>
      <Form.Control size="lg" type="text" name="namereg" placeholder="User Name" />
      <Form.Label htmlFor="emailreg">Email</Form.Label>
      <Form.Control size="lg" type="email" name="emailreg" placeholder="User Email" />
      <Form.Label htmlFor="inputPassword5">Password</Form.Label>
      <Form.Control type="password" id="inputPassword5" aria-describedby="passwordHelpBlock" placeholder="Password" size="lg"/>
      <Form.Text id="passwordHelpBlock" muted>
        Your password must be 8-20 characters long, contain letters and numbers,
        and must not contain spaces, special characters.
      </Form.Text>
      <Form.Label htmlFor="inputPasswordconfirm">Confirm Password</Form.Label>
      <Form.Control type="password" id="inputPasswordconfirm" aria-describedby="passwordHelpBlock" placeholder="Password" size="lg"/>
      
        </Card.Text>
        <Button variant="primary" size="lg">
          Register
        </Button>
      </Card.Body>
    </Card>      
    </div>
    </>
    
  );
}

export default Register;
