import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState, useEffect } from 'react';
import LoadingSpinner from "./LoadingSpinner";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

function Book() {
    const history = useNavigate();
    useEffect(() => {
        if (!window.localStorage.getItem('user-info')) {
            history('/');
        }
    });
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [number, setNumber] = useState('');
    const [message, setMessage] = useState('');
    const [date, setDate] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const theDate = new Date();

    //verify email valid
    function isValidEmail(email) {
        return /\S+@\S+\.\S+/.test(email);
    }
    //validate inputs on change
    const handleChange = (event) => {
        switch (event.target.type) {
            case "email":
                let email = event.target.value;
                // console.warn(email);
                if (!isValidEmail(email)) {
                    event.target.classList.add("is-invalid");
                    event.target.classList.remove("is-valid");
                } else {
                    event.target.classList.remove("is-invalid");
                    event.target.classList.add("is-valid");
                }
                break;
            case "password":
                let password = event.target.value;
                console.warn(password.length);
                if (password.length > 8) {
                    event.target.classList.remove("is-invalid");
                    event.target.classList.add("is-valid");
                } else {
                    event.target.classList.add("is-invalid");
                    event.target.classList.remove("is-valid");
                }
                break;
        }
    };
    const handleFetch = () => {
        setIsLoading(true);
        let data = { 'email': document.getElementById("emailLogin").value, 'password': document.getElementById("inputPassword").value }
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' },
            body: JSON.stringify(data)
        };


        fetch("http://127.0.0.1:8000/api/login", requestOptions)
            .then((respose) => respose.json())
            .then((respose) => {
                //    setUsers(respose.data)
                if (respose.token) {
                    localStorage.setItem('user-info', JSON.stringify(respose));
                    toast("Logged in successfully", {
                        position: toast.POSITION.TOP_CENTER,
                        className: 'toast-success',
                        progressClassName: 'success-progress-bar',
                        autoClose: 2000,
                        toastId: 1
                    });
                    setTimeout(() => { history('/') }, 3000);
                } else {
                    setIsLoading(false);
                    toast("Logged in Failed", {
                        position: toast.POSITION.TOP_CENTER,
                        className: 'toast-error',
                        progressClassName: 'error-progress-bar',
                        autoClose: 2000,
                        toastId: 2
                    });
                }

            })
            .catch(() => {
                setIsLoading(false);
                toast("Unable to send request", {
                    position: toast.POSITION.TOP_CENTER,
                    className: 'toast-error',
                    progressClassName: 'error-progress-bar',
                    autoClose: 2000,
                    toastId: 1
                });
            });
    };
    const renderForm = (
        <div>

            <Card style={{ width: '100%' }}>
                <Card.Body>
                    <Form id='loginform'>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="nameAdd">Name</Form.Label>
                                <Form.Control size="lg" value={name} id="nameAdd" type="text" name="nameAdd" placeholder="User Email" onChange={(e) => setName(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Please enter name.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="emailAdd">Email</Form.Label>
                                <Form.Control size="lg" value={message} id="emailAdd" type="email" name="emailAdd" placeholder="User Email" onChange={(e) => setMessage(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Please enter email.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="contactNum">Contact Number</Form.Label>
                                <Form.Control type="text" value={password} id="contactNum" name="contactNum" placeholder="Contact Number" size="lg" onChange={(e) => setPassword(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Let's try login now!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Password too short.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="message">Message</Form.Label>
                                <Form.Control type="text" value={password} id="message" name="message" placeholder="Message" size="lg" onChange={(e) => setPassword(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Let's try login now!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Password too short.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="date">Date</Form.Label>
                                <Form.Control type="date" value={password} id="date" name="date" placeholder="Date" min={theDate} size="lg" onChange={(e) => setPassword(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Let's try login now!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Password too short.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="startTime">Start Time</Form.Label>
                                <Form.Control type="time" value={password} id="startTime" name="startTime" placeholder="Start Time" size="lg" onChange={(e) => setPassword(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Let's try login now!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Password too short.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="endTime">End Time</Form.Label>
                                <Form.Control type="time" value={password} id="endTime" name="endTime" placeholder="End Time" size="lg" onChange={(e) => setPassword(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Let's try login now!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Password too short.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Button variant="primary" size="lg" onClick={handleFetch}>
                            Login
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </div>);
    return (
        <>
        
        <Header />
