import Header from './Header';
import { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

function Book() {
    const username = JSON.parse(window.localStorage.getItem('user-info'));
    const [isLoading, setIsLoading] = useState(false);
    const date = new Date();
    let day = date.getDate();
    let month = date.getMonth() + 1;
    let year = date.getFullYear();
    
    // This arrangement can be altered based on how we want the date's format to appear.
    let currentDate = `${day}-${month}-${year}`;
    useEffect(() => {
        let data = { 'email': currentDate, 'password': document.getElementById("inputPassword").value }
        const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' },
        body: JSON.stringify(data)
    };
        fetch("http://127.0.0.1:8000/api/login", requestOptions)
            .then((respose) => respose.json())
            .then((respose) => {
                //    setUsers(respose.data)
                if (respose.token) {
                    localStorage.setItem('user-info', JSON.stringify(respose));
                    toast("Logged in successfully", {
                        position: toast.POSITION.TOP_CENTER,
                        className: 'toast-success',
                        progressClassName: 'success-progress-bar',
                        autoClose: 2000,
                        toastId: 1
                    });
                } else {
                    setIsLoading(false);
                    toast("Logged in Failed", {
                        position: toast.POSITION.TOP_CENTER,
                        className: 'toast-error',
                        progressClassName: 'error-progress-bar',
                        autoClose: 2000,
                        toastId: 2
                    });
                }

            })
            .catch(() => {
                setIsLoading(false);
                toast("Unable to send request", {
                    position: toast.POSITION.TOP_CENTER,
                    className: 'toast-error',
                    progressClassName: 'error-progress-bar',
                    autoClose: 2000,
                    toastId: 1
                });
            });
    });
  return (
    <>
    
    <Header />
    <h2>{localStorage.getItem('user-info') ? username.name : ""} you bookings.</h2>
    <form>
                            <Form.Group as={Col} md="3">
                                <Form.Label htmlFor="fromDate">From</Form.Label>
                                <Form.Control size="lg" id="fromDate" type="date" name="fromDate" required />
                                <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Please enter email.</Form.Control.Feedback>
                            </Form.Group>
                            <Form.Group as={Col} md="3">
                                <Form.Label htmlFor="toDate">To</Form.Label>
                                <Form.Control size="lg" id="toDate" type="date" name="toDate" required />
                                <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Please enter email.</Form.Control.Feedback>
                            </Form.Group>
    </form>
        <div id="list">

        </div>
    </>
  );
}

export default Book;
