import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from 'react';

function Login() {
    const [message, setMessage] = useState('');
    //verify email valid
    function isValidEmail(email) {
        return /\S+@\S+\.\S+/.test(email);
    }
    //validate inputs on change
    const handleChange = (event) => {
        switch(event.target.type){
            case "email":
                
        }
        let email = event.target.value;
        // console.warn(email);
        if (!isValidEmail(email)) {
            event.target.classList.add("is-invalid");
        } else {
            event.target.classList.remove("is-invalid");
            event.target.classList.add("is-valid");
        }
    };
    //send request to api
    function processLogin(e) {
        e.preventDefault();
        console.warn('clicked');

    }
    return (
        <div className='col-xs-6 col-sm-6 col-md-6 offset-xs-3 offset-sm-3 offset-md-3 login-form'>
            <h2>Login</h2>
            <Card style={{ width: '100%' }}>
                <Card.Body>
                    <Form>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="emaillogin">Email</Form.Label>
                                <Form.Control size="lg" value={message} id="emailLogin" type="email" name="emaillogin" placeholder="User Email" onChange={(e) => setMessage(e.target.value)} onBlur={handleChange} required />
                                <Form.Control.Feedback type="valid">Looks good!</Form.Control.Feedback>
                                <Form.Control.Feedback type="invalid">Please enter email.</Form.Control.Feedback>
                            </Form.Group>
                        </Row>
                        <Row className="">
                            <Form.Group as={Col} md="12">
                                <Form.Label htmlFor="inputPassword4">Password</Form.Label>
                                <Form.Control type="password" id="inputPassword" aria-describedby="passwordHelpBlock" placeholder="Password" size="lg" required />
                            </Form.Group>
                        </Row>
                        <Button variant="primary" size="lg" onClick={processLogin}>
                            Login
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </div>
    );
}

export default Login;
