import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useState } from 'react';
import LoadingSpinner from "./LoadingSpinner";

function Login() {
    const [message, setMessage] = useState('');
    const [password, setPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const loginform = document.getElementById(loginform);
    //verify email valid
    function isValidEmail(email) {
        return /\S+@\S+\.\S+/.test(email);
    }
    //validate inputs on change
    const handleChange = (event) => {
        switch(event.target.type){
            case "email":
                let email = event.target.value;
                // console.warn(email);
                if (!isValidEmail(email)) {
                    event.target.classList.add("is-invalid");
                    event.target.classList.remove("is-valid");
                } else {
                    event.target.classList.remove("is-invalid");
                    event.target.classList.add("is-valid");
                }
            break;
            case "password":
                let password = event.target.value;
                console.warn(password.length);
                if(password.length > 8){
                    event.target.classList.remove("is-invalid");
                    event.target.classList.add("is-valid");
                } else {
                    event.target.classList.add("is-invalid");
                    event.target.classList.remove("is-valid");
                }
            break;
        }
    };
    const handleFetch = () => {
      setIsLoading(true);
      let data = {'email':document.getElementById("emailLogin").value,'password':document.getElementById("inputPassword").value}
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json','Accept': 'application/json'},
        body: JSON.stringify(data)
        };
        console.warn(data);
        loginform.setAttribute('style','disply:none');
        return;
      fetch("http://127.0.0.1:8000/api/login", requestOptions)
        .then((respose) => respose.json())
        .then((respose) => {
        //    setUsers(respose.data)
           setIsLoading(false)
           // Optional code to simulate delay
           // setTimeout(() => {
           //   setUsers(respose.data);
           //   setIsLoading(false);
           // }, 3000);
        })
        .catch(() => {
        //    setErrorMessage("Unable to fetch user list");
           setIsLoading(false);
           loginform.setAttribute('style','disply:block');
        });
    };
    //send request to api
    function processLogin(e) {
        e.preventDefault();
        console.warn('clicked');

    }
    function form(){
        return 
    }
    return (
        <div className='col-xs-6 col-sm-6 col-md-6 offset-xs-3 offset-sm-3 offset-md-3 login-form'>
            <h2>Login</h2>
      {isLoading ? <LoadingSpinner /> : ""}
            
        </div>
    );
}

export default Login;
